import React, { Component } from "react";
import ReactDOM from "react-dom";
import * as THREE from "three";
import { OrbitControls } from "./OrbitControls.js";
import allQuestions from "./data/all.json";

class ThreeView extends Component {
    onMouseMove(event){
        // calculate mouse position in normalized device coordinates
        // (-1 to +1) for both components
        this.mouse.x = ( $(event.clientX) / window.innerWidth ) * 2 - 1;
        this.mouse.y = - ($(event.clientY) / window.innerHeight ) * 2 + 1;

      }
  onWindowResize = () => {
    this.camera.aspect = window.innerWidth / window.innerHeight;
    this.camera.updateProjectionMatrix();
    this.renderer.setSize(window.innerWidth, window.innerHeight);
  }

  componentDidMount() {
    window.addEventListener("resize", this.onWindowResize, false);

    this.scene = new THREE.Scene();

    //Add Renderer
    this.renderer = new THREE.WebGLRenderer({ antialias: true });
    this.renderer.setSize(window.innerWidth, window.innerWidth);
    this.mount.appendChild(this.renderer.domElement);

    //add Camera
    this.camera = new THREE.PerspectiveCamera(
        45,
        window.innerWidth / window.innerHeight,
        1,
        10000
      );
    this.camera.position.set(0, 2000, 1500);

    // mouse and raycaster
    this.raycaster = new THREE.Raycaster();
    this.mouse = new THREE.Vector2();
    this.raycaster.setFromCamera( this.mouse, this.camera );
    //Camera Controls
    this.controls = new OrbitControls(this.camera, this.renderer.domElement);
    this.controls.update();


    this.INTERSECTED = null;	
    this.cubesToBePicked = [];
    window.addEventListener( 'mousemove', this.onMouseMove, false );
    this.addModels();
    this.renderScene();
    this.start();
  }

  addModels() {

    var worldsize = 6000;
    var plane = new THREE.Mesh(
        new THREE.PlaneGeometry(worldsize, worldsize, 1),
        new THREE.MeshBasicMaterial({
          color: "grey",
          opacity: 0.3,
          transparent: true,
        })
      );
      plane.rotation.set(-Math.PI / 2, 0, 0);
      this.scene.add(plane);
      //grid
      var size = 3000,
        step = 40;
      var geometry, material, line;
      for (let i = -size; i <= size; i += step) {
        if ((i / step) % 2 === 0) {
          continue;
        }
        geometry = new THREE.Geometry();
        material = new THREE.LineBasicMaterial({
          color: "purple",
        });
        geometry.vertices.push(new THREE.Vector3(-size, 0, i));
        geometry.vertices.push(new THREE.Vector3(size, 0, i));
        line = new THREE.LineSegments(geometry, material);
        this.scene.add(line);
      }
      for (let i = -size; i <= size; i += step) {
        if ((i / step) % 2 === 0) {
          continue;
        }
        geometry = new THREE.Geometry();
        material = new THREE.LineBasicMaterial({
          color: "green",
        });
        geometry.vertices.push(new THREE.Vector3(i, 0, -size));
        geometry.vertices.push(new THREE.Vector3(i, 0, size));
        line = new THREE.LineSegments(geometry, material);
        this.scene.add(line);
      }
      var offset = 2000,
      side = 50,
      sidegap = 80;
    for (let i = 0; i < allQuestions.length; i += 1) {
      var questionIndex = Number(allQuestions[i]["id"]) - 1;
      var bottommaterial;
      if (allQuestions[i]["difficulty"] === "1") {
        bottommaterial = new THREE.MeshBasicMaterial({ color: 0x00cc00 });
      } else if (allQuestions[i]["difficulty"] === "2") {
        bottommaterial = new THREE.MeshBasicMaterial({ color: 0xff9900 });
      } else {
        bottommaterial = new THREE.MeshBasicMaterial({ color: 0xD63333 });
      }
      var bottomgeometry = new THREE.BoxGeometry(60, 20, 60);
      var row = Math.floor(questionIndex / side);
      var col = questionIndex % side;
      bottomgeometry.translate(
        col * sidegap - offset,
        10,
        row * sidegap - offset
      );
      var bottommesh = new THREE.Mesh(bottomgeometry, bottommaterial);
      this.scene.add(bottommesh);
      this.cubesToBePicked.push(bottommesh);
    //   window.addEventListener( 'mousemove', onMouseMove, false );


    }


  }



  componentWillUnmount() {
    this.stop();
    this.mount.removeChild(this.renderer.domElement);

  }
  start = () => {
    if (!this.frameId) {
      this.frameId = requestAnimationFrame(this.animate);
    }
  };


  stop = () => {
    cancelAnimationFrame(this.frameId);
  };


  animate = () => {
    this.renderScene();
    this.frameId = window.requestAnimationFrame(this.animate);
  };

  renderScene = () => {
    // calculate objects intersecting the picking ray
    const intersects = this.raycaster.intersectObjects( this.cubesToBePicked );
    if ( intersects.length > 0 )
    {
        // if the closest object intersected is not the currently stored intersection object
        if ( intersects[ 0 ].object != this.INTERSECTED )
        {
            // restore previous intersection object (if it exists) to its original color
            if ( this.INTERSECTED )
                this.INTERSECTED.material.color.setHex( this.INTERSECTED.currentHex );
            // store reference to closest object as current intersection object
            this.INTERSECTED = intersects[ 0 ].object;
            // store color of closest object (for later restoration)
            this.INTERSECTED.currentHex = this.INTERSECTED.material.color.getHex();
            // set a new color for closest object
            this.INTERSECTED.material.color.setHex( 0xFF00FF );

        }

    }
    else // there are no intersections
    {
        // restore previous intersection object (if it exists) to its original color
        if ( this.INTERSECTED ){
            this.INTERSECTED.material.color.setHex( this.INTERSECTED.currentHex );
          }
        // remove previous intersection object reference
        //     by setting current intersection object to "nothing"

        this.INTERSECTED = null;

    }
    if (this.renderer) this.renderer.render(this.scene, this.camera);
    this.controls.update();


  };

  render() {
    return (
      <div
        ref={mount => {
          this.mount = mount;
        }}
      />
    );
  }
}
export default ThreeView;
